import "./App.css";
import io from "socket.io-client";

const socket = io.connect("http://localhost:3001");
function App() {
  const sendMessage = () => {
    socket.emit("send_message", {
      message: "hello world",
    });
  };
  return (
    <div className="App">
      <input placeholder="message..."></input>{" "}
      <button onClick={sendMessage}>Send Message</button>
    </div>
  );
}

export default App;
